import React, { useEffect } from 'react';
import { Carousel } from 'react-bootstrap';  // Import Bootstrap Carousel

const BestSelling = () => {
  const products = [
    { id: 1, name: 'Summer Wind T-shirt', price: '$31.99', rating: 4.2, image: 'https://via.placeholder.com/200' },
    { id: 2, name: 'Tailored Jacket', price: '$41.03', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 3, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
    { id: 4, name: 'Classic Top', price: '$24.54', rating: 3.8, image: 'https://via.placeholder.com/200' },
    { id: 5, name: 'White Solid T-shirt', price: '$42.09', rating: 5.0, image: 'https://via.placeholder.com/200' },
    // { id: 6, name: 'Formal Jeans', price: '$38.99', rating: 5.0, image: 'https://via.placeholder.com/200' },
    // { id: 7, name: 'Product 7', price: '$38.99', rating: 5.0, image: 'https://via.placeholder.com/200' },
    // { id: 8, name: 'Product 8', price: '$56.59', rating: 5.0, image: 'https://via.placeholder.com/200' },
    // // Add more products as needed
  ];

  // Inline styles for the various elements
  const sectionStyle = {
    marginTop: '3rem',
    marginBottom: '3rem',
  };

  const textCenterStyle = {
    textAlign: 'center',
    marginBottom: '2rem',
  };

  const headingStyle = {
    fontWeight: 'bold',
    fontSize: '2rem',
  };

  const subtitleStyle = {
    color: '#6c757d', // Bootstrap muted text color
  };

  const carouselItemStyle = {
    padding: '15px',
  };

  const carouselImageStyle = {
    width: '100%',
    height: 'auto',
  };

  // Custom behavior to slide one item at a time
  useEffect(() => {
    const items = document.querySelectorAll('.carousel .carousel-item');

    items.forEach((el) => {
      const minPerSlide = 4;
      let next = el.nextElementSibling;
      for (let i = 1; i < minPerSlide; i++) {
        if (!next) {
          // wrap carousel by using first child
          next = items[0];
        }
        const cloneChild = next.cloneNode(true);
        el.appendChild(cloneChild.children[0]);
        next = next.nextElementSibling;
      }
    });
  }, []);

  return (
    <div className="container my-5" style={sectionStyle}>
      {/* Centered Title and Subtitle */}
      <div style={textCenterStyle}>
        <h2 style={headingStyle}>Best Selling</h2>
        <p style={subtitleStyle}>Get in on the trend with our curated selection of best-selling styles</p>
      </div>

      {/* Product Carousel */}
      <div id="bestSellingCarousel" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active">
            <div className="row">
              {products.slice(0, 4).map((product) => (
                <div key={product.id} className="col-lg-3 col-md-4 col-sm-6">
                  <div style={carouselItemStyle}>
                    <img
                      src={product.image}
                      alt={product.name}
                      style={carouselImageStyle}
                    />
                  </div>
                  <div className="text-center">
                    <h5>{product.name}</h5>
                    <p>{product.price}</p>
                    <p>Rating: {product.rating}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Add more carousel-item here with additional products */}
          <div className="carousel-item">
            <div className="row">
              {products.slice(4, 8).map((product) => (
                <div key={product.id} className="col-lg-3 col-md-4 col-sm-6">
                  <div style={carouselItemStyle}>
                    <img
                      src={product.image}
                      alt={product.name}
                      style={carouselImageStyle}
                    />
                  </div>
                  <div className="text-center">
                    <h5>{product.name}</h5>
                    <p>{product.price}</p>
                    <p>Rating: {product.rating}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Carousel Controls */}
        <button className="carousel-control-prev" type="button" data-bs-target="#bestSellingCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#bestSellingCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>

      {/* See More Button */}
      <div className="text-center mt-4">
        <a href="#" className="btn btn-primary">
          See More <i className="bi bi-arrow-right"></i> {/* Arrow Icon */}
        </a>
      </div>
    </div>
  );
};

export default BestSelling;
