import React from 'react';
import { Link } from 'react-router-dom';
import { BsBag } from "react-icons/bs";

const Navbar = () => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light" style={{ 
      background: 'linear-gradient(135deg, #EFE8C2, #F7F3E7)', 
    }}>
      <div className="container d-flex justify-content-between align-items-center">
        
        {/* Links: Home, Shop, About Us, Contact, Cart, Login */}
        <div className="d-flex align-items-center" style={{ gap: '20px' }}> {/* Flex container with 20px gap */}
          <Link 
            className="nav-link" 
            to="/" 
            style={{ 
              color: '#224F34', 
              fontFamily: 'Poppins', 
              fontWeight: 'bold', 
              fontSize: '1.2rem',
              textDecoration: 'none', 
            }}
            onMouseOver={(e) => e.currentTarget.style.color = 'white'}
            onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
          >
            HOME
          </Link>
          <Link 
            className="nav-link" 
            to="/shop" 
            style={{ 
              color: '#224F34', 
              fontFamily: 'Poppins', 
              fontWeight: 'bold', 
              fontSize: '1.2rem',
              textDecoration: 'none', 
            }}
            onMouseOver={(e) => e.currentTarget.style.color = 'white'}
            onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
          >
            SHOP
          </Link>
          <Link 
            className="nav-link" 
            to="/about" 
            style={{ 
              color: '#224F34', 
              fontFamily: 'Poppins', 
              fontWeight: 'bold', 
              fontSize: '1.2rem',
              textDecoration: 'none', 
            }}
            onMouseOver={(e) => e.currentTarget.style.color = 'white'}
            onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
          >
            ABOUT US
          </Link>
          <Link 
            className="nav-link" 
            to="/contact" 
            style={{ 
              color: '#224F34', 
              fontFamily: 'Poppins', 
              fontWeight: 'bold', 
              fontSize: '1.2rem',
              textDecoration: 'none', 
            }}
            onMouseOver={(e) => e.currentTarget.style.color = 'white'}
            onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
          >
            CONTACT
          </Link>
          <Link 
            to="/cart" 
            className="nav-link position-relative" 
            style={{ 
              color: '#224F34', 
              fontWeight: 'bold', 
              fontSize: '1.2rem',
              textDecoration: 'none', 
            }}>
            <BsBag size={28} />
            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
              3
            </span>
          </Link>
          <Link 
            to="/login" 
            className="btn btn-outline-success" 
            style={{ 
              color: '#224F34', 
              borderColor: '#224F34', 
              fontWeight: 'bold', 
              fontSize: '1.2rem', 
            }}
          >
            Login
          </Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
