import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import './ProductDetail.css'; // Make sure you have this stylesheet

const ProductDetail = () => {
  const { id } = useParams(); // Extract product ID from URL
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [activeTab, setActiveTab] = useState('details');
  
  // Simulate product fetch based on ID
  const productData = getProductById(id);
  const [mainImage, setMainImage] = useState(productData.images[0]);
  const similarProducts = getSimilarProducts(productData.id);

  // Slider settings for react-slick carousel
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };

  return (
    <div className="product-detail-container">
      <div className="product-detail">
        <div className="product-images">
          <img src={mainImage} alt={productData.name} className="main-image" />
          <div className="image-swatches">
            {productData.images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`${productData.name} ${index + 1}`}
                onClick={() => setMainImage(image)}
                className="swatch"
              />
            ))}
          </div>
        </div>

        <div className="product-info">
          <h1 className="product-title">{productData.name}</h1>
          <p className="brand">Brand: {productData.brand}</p>
          <p className="seller">Sold by: {productData.seller}</p>
          <p className="rating">Rating: {productData.rating} (Reviews: {productData.reviews})</p>
          <div className="pricing">
            <span className="sale-price">Rs. {productData.salePrice}</span>
            <span className="original-price">Rs. {productData.price}</span>
            <span className="discount">30% off</span>
          </div>

          {/* Size Selection */}
          <div className="size-selection">
            <label>Select Size</label>
            <div>
              {['S', 'M', 'L', 'XL'].map((size) => (
                <button
                  key={size}
                  className={`size-button ${selectedSize === size ? 'active' : ''}`}
                  onClick={() => setSelectedSize(size)}
                >
                  {size}
                </button>
              ))}
            </div>
            <a href="/size-chart" className="size-chart-link">Size Chart</a>
          </div>

          {/* Color Selection */}
          <div className="color-selection">
            <label>Select Color</label>
            <div>
              {productData.colors.map((color) => (
                <button
                  key={color}
                  className={`color-swatch ${selectedColor === color ? 'active' : ''}`}
                  onClick={() => setSelectedColor(color)}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
          </div>

          {/* Offers */}
          <div className="offers">
            <h3>Best Offers</h3>
            <ul>
              <li>Special offer: Get 25% off T&C</li>
              <li>Bank offer: Get 30% off on Axis Bank Credit card T&C</li>
              <li>Wallet offer: Get 40% cashback via Paytm wallet T&C</li>
            </ul>
          </div>

          {/* Actions */}
          <div className="actions">
            <button className="add-to-cart">Add to Cart</button>
            <button className="wishlist-icon">â™¥</button>
          </div>
        </div>
      </div>

      {/* Tabs Section */}
      <div className="tabs">
        <div className="tab-headings">
          <div className={`tab ${activeTab === 'details' ? 'active' : ''}`} onClick={() => setActiveTab('details')}>Product Details</div>
          <div className={`tab ${activeTab === 'specs' ? 'active' : ''}`} onClick={() => setActiveTab('specs')}>Specifications</div>
          <div className={`tab ${activeTab === 'reviews' ? 'active' : ''}`} onClick={() => setActiveTab('reviews')}>Ratings and Reviews</div>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {activeTab === 'details' && <p>{productData.details}</p>}
          {activeTab === 'specs' && <p>{productData.specs}</p>}
          {activeTab === 'reviews' && <p>{productData.reviews}</p>}
        </div>
      </div>

      {/* Similar Products Carousel */}
      <div className="similar-products">
        <h2>Similar Products</h2>
        <Slider {...sliderSettings}>
          {similarProducts.map((product) => (
            <div key={product.id} className="carousel-item">
              <img src={product.image} alt={product.name} className="carousel-image" />
              <h5>{product.name}</h5>
              <p>Brand: {product.brand}</p>
              <div>
                <span className="sale-price">Rs. {product.salePrice}</span>
                <span className="original-price">Rs. {product.price}</span>
                <span className="discount">(30% off)</span>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

// Dummy function to simulate fetching product by ID
function getProductById(id) {
  return {
    id,
    name: 'Stylish Shirt',
    brand: 'Brand A',
    seller: 'Seller XYZ',
    rating: 4.5,
    reviews: 120,
    price: 99.99,
    salePrice: 69.99,
    images: [
      '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg',
      '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg',
    ],
    colors: ['#ff0000', '#00ff00', '#0000ff'],
    details: 'This is a stylish shirt made of 100% cotton.',
    specs: 'Material: Cotton, Size: S/M/L/XL, Care: Machine wash',
  };
}

// Dummy function to simulate fetching similar products
function getSimilarProducts(currentProductId) {
  return [
    {
      id: '2',
      name: 'Casual Shirt',
      brand: 'Brand A',
      price: 79.99,
      salePrice: 59.99,
      rating: 4.7,
      image: '/images/casual-shirt.jpg',
    },
    {
      id: '3',
      name: 'Formal Pants',
      brand: 'Brand B',
      price: 99.99,
      salePrice: 69.99,
      rating: 4.5,
      image: '/images/formal-pants.jpg',
    },
    {
      id: '4',
      name: 'Summer Dress',
      brand: 'Brand C',
      price: 59.99,
      salePrice: 49.99,
      rating: 4.8,
      image: '/images/summer-dress.jpg',
    },
  ];
}

export default ProductDetail;
