import React from 'react';
import { Carousel } from 'react-bootstrap';  

const BestSelling = () => {
  const products = [
    { id: 1, name: 'Summer Wind T-shirt', price: '$31.99', rating: 4.2, image: 'https://via.placeholder.com/200' },
    { id: 2, name: 'Tailored Jacket', price: '$41.03', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 3, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
    { id: 4, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
];

const sectionStyle = {
    marginTop: '3rem',
    marginBottom: '3rem',
  };

  const textCenterStyle = {
    textAlign: 'center',
    marginBottom: '2rem',
  };

  const headingStyle = {
    fontWeight: 'bold',
    fontSize: '2rem',
  };

  const subtitleStyle = {
    color: '#6c757d', // Bootstrap muted text color
  };

  const carouselItemStyle = {
    display: 'flex',
    justifyContent: 'center',
  };

  const carouselImageStyle = {
    width: '200px',
    height: 'auto',
  };

  const buttonStyle = {
    marginTop: '2rem',
  };

  const arrowIconStyle = {
    marginLeft: '0.5rem',
  };

  return (
    <div className="container" style={sectionStyle}>
      {/* Centered Title and Subtitle */}
      <div style={textCenterStyle}>
        <h2 style={headingStyle}>Best Selling</h2>
        <p style={subtitleStyle}>Get in on the trend with our curated selection of best-selling styles</p>
      </div>

      {/* Product Carousel */}
      <Carousel>
        {products.map((product) => (
          <Carousel.Item key={product.id}>
            <div style={carouselItemStyle}>
              <img
                src={product.image}
                alt={product.name}
                style={carouselImageStyle}
              />
            </div>
            <Carousel.Caption className="text-dark">
              <h5>{product.name}</h5>
              <p>{product.price}</p>
              <p>Rating: {product.rating}</p>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>

      {/* See More Button */}
      <div className="text-center" style={buttonStyle}>
        <a href="#" className="btn btn-primary">
          See More <i className="bi bi-arrow-right" style={arrowIconStyle}></i> {/* Arrow Icon */}
        </a>
      </div>
    </div>
  );
};

export default BestSelling;
