// src/components/ProductCard.jsx
import React from 'react';
import { BsHeart } from 'react-icons/bs';

const ProductCard = ({ product }) => {
  return (
    <div className="product-card shadow-sm p-3 mb-4 rounded" style={{ border: '1px solid #ccc', borderRadius: '10px' }}>
      <img
        src={product.image}
        alt={product.name}
        style={{ width: '100%', height: '200px', objectFit: 'cover', borderRadius: '10px' }}
      />
      <h5 className="mt-3">{product.name}</h5>
      <div className="d-flex justify-content-between align-items-center">
        <span className="text-success">${product.salePrice}</span>
        <span className="text-muted text-decoration-line-through">${product.price}</span>
      </div>
      <button className="btn btn-outline-danger mt-2" style={{ width: '100%' }}>
        Add to Cart
      </button>
      <button className="btn btn-light mt-2" style={{ width: '100%' }}>
        <BsHeart /> Add to Wishlist
      </button>
    </div>
  );
};

export default ProductCard;
