import React, { useState } from 'react';
import { Tab, Tabs } from 'react-bootstrap'; // Import Bootstrap Tabs

const BestSelling2 = () => {
  const products = {
    sale: [
      { id: 1, name: 'Discounted T-shirt', price: '$19.99', rating: 4.5, image: 'https://via.placeholder.com/200' },
      { id: 2, name: 'Sale Jacket', price: '$35.00', rating: 4.8, image: 'https://via.placeholder.com/200' },
      { id: 3, name: 'Bargain Suit', price: '$45.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
      { id: 4, name: 'Special Top', price: '$29.99', rating: 4.3, image: 'https://via.placeholder.com/200' },
      { id: 5, name: 'Promo T-shirt', price: '$20.00', rating: 4.2, image: 'https://via.placeholder.com/200' },
      { id: 6, name: 'Sale Jeans', price: '$30.00', rating: 4.6, image: 'https://via.placeholder.com/200' },
    ],
    hot: [
      { id: 7, name: 'Hot Item 1', price: '$25.99', rating: 5.0, image: 'https://via.placeholder.com/200' },
      { id: 8, name: 'Hot Item 2', price: '$30.00', rating: 4.9, image: 'https://via.placeholder.com/200' },
      { id: 9, name: 'Hot Item 3', price: '$22.50', rating: 4.7, image: 'https://via.placeholder.com/200' },
      { id: 10, name: 'Hot Item 4', price: '$18.99', rating: 4.8, image: 'https://via.placeholder.com/200' },
    ],
    newArrival: [
      { id: 11, name: 'New T-shirt', price: '$31.99', rating: 4.2, image: 'https://via.placeholder.com/200' },
      { id: 12, name: 'New Jacket', price: '$41.03', rating: 5.0, image: 'https://via.placeholder.com/200' },
      { id: 13, name: 'New Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
      { id: 14, name: 'New Top', price: '$24.54', rating: 3.8, image: 'https://via.placeholder.com/200' },
    ],
    accessories: [
      { id: 15, name: 'Accessory 1', price: '$12.99', rating: 4.2, image: 'https://via.placeholder.com/200' },
      { id: 16, name: 'Accessory 2', price: '$15.99', rating: 4.5, image: 'https://via.placeholder.com/200' },
      { id: 17, name: 'Accessory 3', price: '$19.99', rating: 4.7, image: 'https://via.placeholder.com/200' },
      { id: 18, name: 'Accessory 4', price: '$22.50', rating: 4.3, image: 'https://via.placeholder.com/200' },
    ],
  };

  const [key, setKey] = useState('sale'); // Default selected tab

  // Tab styles
  const tabStyle = {
    border: 'none',
    color: 'black',
  };

  const activeTabStyle = {
    borderBottom: '2px solid green', // Underline for active tab
    color: '#6F6A42', // Active tab color
  };

  return (
    <div className="container my-5">
      {/* Main Heading */}
      <h2 className="fw-bold text-center mb-4" style={{ fontSize: '2.5rem', color: '#6F6A42', fontFamily: 'Poppins' }}>
        Best Selling
      </h2>

      {/* Categories Tabs */}
      <Tabs
        activeKey={key}
        onSelect={(k) => setKey(k)}
        className="mb-4"
        justify
      >
        <Tab eventKey="sale" title={<span style={key === 'sale' ? activeTabStyle : tabStyle}>Sale</span>} />
        <Tab eventKey="hot" title={<span style={key === 'hot' ? activeTabStyle : tabStyle}>Hot</span>} />
        <Tab eventKey="newArrival" title={<span style={key === 'newArrival' ? activeTabStyle : tabStyle}>New Arrival</span>} />
        <Tab eventKey="accessories" title={<span style={key === 'accessories' ? activeTabStyle : tabStyle}>Accessories</span>} />
      </Tabs>

      {/* Products for the selected tab */}
      <div className="row">
        {products[key].map((product) => ( // Display products based on selected tab
          <div key={product.id} className="col-lg-3 col-md-4 col-sm-6 mb-4">
            <div className="card border-0 rounded" style={{ overflow: 'hidden' }}>
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top img-fluid"
                style={{ height: '200px', objectFit: 'cover', borderRadius: '10px 10px 0 0' }} // Rounded corners for the image
              />
              <div className="card-body text-center">
                <h5 className="card-title" style={{ fontSize: '1.5rem', fontFamily: 'Libre Baskerville', fontWeight: 'bold' }}>{product.name}</h5>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                  <p className="card-text" style={{ fontSize: '1.2rem', fontFamily: 'Libre Baskerville', fontWeight: 'bold', margin: '0 10px 0 0' }}>{product.price}</p>
                  <div style={{ height: '20px', borderLeft: '1px solid #ccc', margin: '0 10px' }} />
                  <p className="card-rating" style={{ fontSize: '1.2rem', fontFamily: 'Libre Baskerville', fontWeight: 'bold', margin: '0' }}>Rating: {product.rating}</p>
                </div>
                <a href="#" className="btn btn-primary">Add to Cart</a>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* See More Button */}
      <div className="text-center mt-4">
        <a href="#" className="btn btn-secondary">
          See More <i className="bi bi-arrow-right"></i> {/* Arrow Icon */}
        </a>
      </div>
    </div>
  );
};

export default BestSelling2;
