import React, { useState } from 'react';
import { BsHeart } from 'react-icons/bs';

const ProductCard = ({ product, onClick  }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
   <>
    <div className="product-card" onClick={onClick} style={{ cursor: 'pointer' }}></div>
    <img src={product.image} alt={product.name} />
    <h4>{product.name}</h4>
      <p>${product.salePrice} <span className="original-price">${product.price}</span></p>
    

    <div
      className="product-card"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <img
        src={product.image}
        alt={product.name}
        className={`product-image ${isHovered ? 'hovered' : ''}`}
      />

      <div className="wishlist-icon">
        <BsHeart size={20} />
      </div>

      {isHovered && (
        <div className="product-info">
          <span className="product-name">{product.name}</span>
          <span className="product-sale-price">${product.salePrice}</span>
          <span className="product-original-price">${product.price}</span>
          <button className="add-to-cart-button">Add to Cart</button>
        </div>
      )}
    </div>
    </>
  );
};

export default ProductCard;
