import React from 'react';
import { FaStar, FaQuoteLeft, FaStarHalfAlt, FaRegStar } from 'react-icons/fa'; // Import full, half, and empty stars
import './Testimonial.css'; // Custom styles
import Slider from 'react-slick';

const Feedback = () => {
  const testimonials = [
    {
      id: 1,
      name: 'Beenish Mumtaz',
      feedback: 'I absolutely love the quality and style of the clothing. The fit is perfect!',
      rating: 5,
      image: 'https://via.placeholder.com/100', // Placeholder image for the customer
    },
    {
      id: 2,
      name: 'Maria Shabbir',
      feedback: 'The customer experience was exceptional from start to finish. Highly recommend!',
      rating: 4.5,
      image: 'https://via.placeholder.com/100',
    },
    {
      id: 3,
      name: 'Ayra Khan',
      feedback: 'The clothes I bought are fashionable and comfortable. Will shop again!',
      rating: 4.8,
      image: 'https://via.placeholder.com/100',
    },
    {
        id: 4,
        name: 'Ayra Khan',
        feedback: 'The clothes I bought are fashionable and comfortable. Will shop again!',
        rating: 4.8,
        image: 'https://via.placeholder.com/100',
    },
  ];

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      if (i <= Math.floor(rating)) {
        // Full star
        stars.push(<FaStar key={i} className="star full-star" />);
      } else if (i === Math.ceil(rating) && rating % 1 !== 0) {
        // Half star
        stars.push(<FaStarHalfAlt key={i} className="star half-star" />);
      } else {
        // Empty star
        stars.push(<FaRegStar key={i} className="star empty-star" />);
      }
    }
    return stars;
  };

  const settings = {
    dots: true,               // Show dots for navigation
    infinite: true,           // Loop through the testimonials infinitely
    speed: 500,               // Transition speed
    slidesToShow: 1,          // Show 1 testimonial at a time
    slidesToScroll: 1,        // Scroll 1 testimonial at a time
    autoplay: true,           // Automatically scroll through the testimonials
    autoplaySpeed: 3000,      // Autoplay speed (3 seconds)
    cssEase: 'ease-in-out',   // Smooth transitions
  };


  return (
    <div className="container my-5">
      <h2 className="fw-bold text-center mb-4">What Our Customers Say</h2>
      <p className="text-center text-muted mb-5">
        Real experiences shared by our valued customers.
      </p>
     <Slider {...settings}>  
        {testimonials.map((testimonial) => (
          <div key={testimonial.id} className="row col-lg-4 col-md-6 mb-4">
            <div className="testimonial-card shadow-sm rounded text-center p-4">
              <FaQuoteLeft className="quote-icon mb-3" style={{ fontSize: '2rem', color: '#ff5722' }} />
              <p className="testimonial-feedback mb-4">"{testimonial.feedback}"</p>
              <div className="testimonial-author">
                {/* <img
                  src={testimonial.image}
                  alt={testimonial.name}
                  className="rounded-circle mb-3"
                  style={{ width: '80px', height: '80px' }}
                /> */}
                <h5 className="author-name mb-0">{testimonial.name}</h5>
                <div className="testimonial-rating mt-2">
                {renderStars(testimonial.rating)}

                  {/* {[...Array(Math.floor(testimonial.rating))].map((_, i) => (
                    <FaStar key={i} style={{ color: '#ffc107' }} />
                  ))}
                  {testimonial.rating % 1 !== 0 && <FaStar style={{ color: '#ffc107' }} />} */}
                </div>
              </div>
            </div>
          </div>
        ))}
        </Slider>
      </div>
  );
};


export default Feedback;
