import React, { useState } from 'react';
import Slider from 'react-slick'; // Import React Slick

const BestSelling = () => {
  const products = [
    { id: 1, name: 'Summer Wind T-shirt', price: '$31.99', rating: 4.2, image: '/images/IMG-20240723-WA0021.jpg' },
    { id: 2, name: 'Tailored Jacket', price: '$41.03', rating: 5.0, image: '/images/IMG-20240723-WA0022.jpg' },
    { id: 3, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: '/images/IMG-20240723-WA0023.jpg' },
    { id: 4, name: 'Classic Top', price: '$24.54', rating: 3.8, image: '/images/IMG-20240723-WA0024.jpg' },
    { id: 5, name: 'White Solid T-shirt', price: '$42.09', rating: 5.0, image: '/images/IMG-20240723-WA0025.jpg' },
    { id: 6, name: 'Formal Jeans', price: '$38.99', rating: 5.0, image: '/images/IMG-20240723-WA0026.jpg' },
    { id: 7, name: 'Product 7', price: '$38.99', rating: 5.0, image: '/images/IMG-20240723-WA0027.jpg' },
    { id: 8, name: 'Product 8', price: '$56.59', rating: 5.0, image: '/images/IMG-20240723-WA0028.jpg' },
  ];

  // State to manage hover for each product button
  const [hoverStates, setHoverStates] = useState(Array(products.length).fill(false));

  // Slick Carousel settings
  const settings = {
    autoplay: true,
    autoplaySpeed: 2000,
    cssEase: "ease-in-out",
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };

  const carouselItemStyle = {
    padding: '15px',
  };

  const carouselImageStyle = {
    width: '100%',
    height: '400px',
    objectFit: 'cover',
  };

  const headingStyle = {
    color: '#6F6A42',
    fontFamily: 'Libre Baskerville, serif',
    textAlign: 'center',
  };

  const productNameStyle = {
    textAlign: 'center',
    fontFamily: 'Libre Baskerville, serif',
  };

  const priceRatingStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: '10px',
    marginTop: '10px',
    fontFamily: 'Libre Baskerville, serif',
  };

  const priceStyle = {
    fontWeight: 'bold',
    fontSize: '1.2rem',
  };

  const ratingStyle = {
    fontWeight: 'bold',
    fontSize: '1.2rem',
    marginLeft: '10px',
  };

  const separatorStyle = {
    height: '20px',
    borderLeft: '1px solid #ccc',
    margin: '0 10px',
  };

  // Function to handle hover events
  const handleMouseEnter = (index) => {
    const newHoverStates = [...hoverStates];
    newHoverStates[index] = true;
    setHoverStates(newHoverStates);
  };

  const handleMouseLeave = (index) => {
    const newHoverStates = [...hoverStates];
    newHoverStates[index] = false;
    setHoverStates(newHoverStates);
  };

  return (
    <div className="container-fluid my-5">
      {/* Centered Title and Subtitle */}
      <div className="text-center mb-5">
        <h2 style={headingStyle}>Best Selling</h2>
        <p className="text-black">Get in on the trend with our curated selection of best-selling styles</p>
      </div>

      {/* React Slick Carousel */}
      <Slider {...settings}>
        {products.map((product, index) => {
          const buttonStyle = {
            backgroundColor: hoverStates[index] ? '#d3d3d3' : 'white', // Light gray on hover
            color: '#6F6A42',
            border: '1px solid #224F34',
            padding: '12px 24px',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1.2rem',
            fontWeight: 'bold',
            display: 'block',
            margin: '20px auto',
            transition: 'background-color 0.3s ease', // Smooth hover transition
          };

          return (
            <div key={product.id}>
              <div style={carouselItemStyle}>
                <img
                  src={product.image}
                  alt={product.name}
                  style={carouselImageStyle}
                />
              </div>
              <div className="text-center" style={productNameStyle}>
                <h5>{product.name}</h5>
                <div style={priceRatingStyle}>
                  <span style={priceStyle}>{product.price}</span>
                  <div style={separatorStyle} />
                  <span style={ratingStyle}>{product.rating} ⭐</span>
                </div>
              </div>
              <button
                style={buttonStyle}
                onMouseEnter={() => handleMouseEnter(index)}
                onMouseLeave={() => handleMouseLeave(index)}
              >
                View Product
              </button>
            </div>
          );
        })}
      </Slider>

      {/* See More Button */}
      <div className="text-center mt-4 p-5">
        <a href="#" className="btn btn-primary ">
          See More <i className="bi bi-arrow-right"></i> {/* Arrow Icon */}
        </a>
      </div>
    </div>
  );
};

export default BestSelling;
