import { FaFacebookF, FaTwitter, FaInstagram, FaLinkedin } from 'react-icons/fa';
import React, { useState } from 'react';

const Footer = () => {
  const [email, setEmail] = useState('');
  const [hovered, setHovered] = useState(false); // State for button hover effect

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Subscribed with email:', email);
    setEmail(''); // Clear the input field after submission
  };

  const footerStyle = {
    backgroundColor: '#333',
    color: '#f8f9fa',
    padding: '60px 0',
    borderTop: '1px solid #444',
    fontFamily: 'Poppins, sans-serif',
  };

  const footerTitleStyle = {
    fontWeight: 'bold',
    fontSize: '1.25rem',
    marginBottom: '20px',
    color: '#f8f9fa',
  };

  const inputFormStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  };

  const inputStyle = {
    padding: '12px',
    borderRadius: '5px',
    border: '1px solid #555',
    backgroundColor: '#444',
    color: '#fff',
  };

  // Dynamically set the button background based on hover state
  const buttonStyle = {
    padding: '12px 20px',
    backgroundColor: hovered ? '#e2e6ea' : '#f8f9fa', // Change color on hover
    color: '#333',
    border: 'none',
    borderRadius: '5px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  };

  const socialContainerStyle = {
    display: 'flex',
    gap: '10px',
    justifyContent: 'center',
    marginTop: '20px',
  };

  return (
    <footer style={footerStyle}>
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-6 mb-4">
            <h5 style={footerTitleStyle}>LOGO</h5>
            <p style={{ color: '#ccc', lineHeight: '1.8' }}>
              Your go-to store for the latest fashion trends. We bring you the best clothing with the finest quality.
            </p>
          </div>

          {/* Newsletter Section */}
          <div className="col-lg-3 col-md-6 mb-4">
            <h5 style={footerTitleStyle}>Stay Up To Date</h5>
            <form onSubmit={handleSubmit} style={inputFormStyle}>
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                style={inputStyle}
              />
              <button
                type="submit"
                style={buttonStyle}
                onMouseEnter={() => setHovered(true)}
                onMouseLeave={() => setHovered(false)}
              >
                Subscribe
              </button>
            </form>
          </div>
        </div>

        {/* Copyright Section */}
        <div className="text-center py-4" style={{ borderTop: '1px solid #444', marginTop: '40px' }}>
          <p className="text-muted mb-0">
            &copy; {new Date().getFullYear()} Your Brand. All rights reserved.
          </p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
