// src/components/ProductCard.jsx
import React, { useState } from 'react';
import { BsHeart } from 'react-icons/bs';

const ProductCard = ({ product }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="product-card shadow-sm rounded"
      style={{
        border: '1px solid #ccc',
        borderRadius: '10px',
        position: 'relative',
        overflow: 'hidden',
        height: '300px', // Increased height
        transition: 'transform 0.3s',
        marginBottom: '20px',
        cursor: 'pointer',
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Product Image with scaling effect */}
      <img
        src={product.image}
        alt={product.name}
        style={{
          width: '100%',
          height: '500px',
          objectFit: 'cover',
          borderRadius: '10px',
          transition: 'transform 0.3s',
        }}
      />
      {/* Wishlist Icon Overlay */}
      <div style={{
        position: 'absolute',
        top: '10px',
        right: '10px',
        backgroundColor: 'rgba(255, 255, 255, 0.8)',
        borderRadius: '50%',
        padding: '10px',
        cursor: 'pointer',
      }}>
        <BsHeart size={20} color="#e74c3c" />
      </div>
      
      {/* Title, Price and Button on Hover */}
      {isHovered && (
        <div style={{
          position: 'absolute',
          bottom: '0',
          left: '0',
          right: '0',
          backgroundColor: '#ffffff',
          padding: '10px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'flex-start',
          opacity: '0.9',
        }}>
          <span style={{ fontWeight: 'bold', color: '#6F6A42', marginBottom: '5px' }}>{product.name}</span>
          <span style={{ fontWeight: 'bold', color: '#6F6A42' }}>${product.salePrice}</span>
          <span style={{ textDecoration: 'line-through', color: 'grey' }}>${product.price}</span>
          <button className="btn btn-outline-danger" style={{ borderRadius: '5px', marginTop: '5px', width: '100%' }}>
            Add to Cart
          </button>
        </div>
      )}
    </div>
  );
};

export default ProductCard;
