import React from 'react';

const ProductShowcase = () => {
  const products = [
    {
      id: 1,
      name: 'Stylish Jacket',
      description: 'Stay warm and stylish with our latest jacket that combines comfort with fashion.',
      image: 'https://via.placeholder.com/300',
    },
    {
      id: 2,
      name: 'Trendy Sneakers',
      description: 'Step into comfort with our trendy sneakers, perfect for any occasion.',
      image: 'https://via.placeholder.com/300',
    },
    {
      id: 3,
      name: 'Casual T-Shirt',
      description: 'A versatile t-shirt that pairs well with any outfit, made from high-quality fabric.',
      image: 'https://via.placeholder.com/300',
    },
  ];

  return (
    <div className="container my-5">
      <h2 className="fw-bold text-center mb-4">Featured Products</h2>
      <p className="text-center mb-5">
        Discover our selection of trending products that blend style and comfort. 
        Each piece is designed to elevate your wardrobe.
      </p>

      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-lg-4 col-md-6 mb-4">
            <div className="card shadow-sm border-light rounded text-center">
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top img-fluid"
                style={{ borderRadius: '10px 10px 0 0' }} // Rounded corners for the image
              />
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.description}</p>
                <a href="#" className="btn btn-primary">View Product</a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductShowcase;
