import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Home from './pages/home';
import About from './pages/about';
import Navbar from './components/navbar';
import Navbar2 from './components/navbar2'; // Import the second navbar
import Footer from './components/Footer';
import Shop from './pages/Shop';
import ProductDetail from './pages/ProductDetail';
import ContactPage from './pages/Contact';
import Login from './pages/LoginPage';
// A custom hook to determine which navbar to display
const App = () => {
  const location = useLocation();

  // Determine if the current path is the home page
  const isHomePage = location.pathname === '/';

  return (
    <>
      {isHomePage ? <Navbar /> : <Navbar2 />} {/* Conditionally render the navbar */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
         <Route path="/shop" element={<Shop/>} />
        <Route path="/contact" element={<ContactPage />} />
         <Route path="/product/:id" element={<ProductDetail />} />
        {/* <Route path="/cart" element={<Cart />} /> */}
        <Route path="/login" element={<Login />} />
      </Routes>
      <Footer />
    </>
  );
};

const Main = () => (
  <Router>
    <App />
  </Router>
);

export default Main;
