import React from 'react'

const HeroSection = () => {
    // Inline styles in a JavaScript object
    const heroSectionStyle = {
      minHeight: '80vh',
      display: 'flex',
      alignItems: 'center',
      padding: '40px 0',
    };
  
    const heroImageStyle = {
      maxWidth: '100%',
      height: 'auto',
    };


  return (
    <section className="container" style={heroSectionStyle}>
      <div className="row align-items-center">
        {/* Left: Text Section */}
        <div className="col-lg-6">
          <h1 className="display-4 fw-bold">Discover Amazing Products</h1>
          <h4 className="my-4">Find the perfect item that suits your style</h4>
          <a href="#" className="btn btn-primary btn-lg">Explore Now</a>
        </div>

        {/* Right: Image Section */}
        <div className="col-lg-6 text-center">
          <img
            src="https://via.placeholder.com/500x400"
            alt="Product Image"
            style={heroImageStyle}
          />
        </div>
      </div>
    </section>
      );
    };

export default HeroSection
