// src/pages/Shop.jsx
import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import Pagination from '../components/Pagination';

const Shop = () => {
  const [products] = useState([
    { id: 1, name: 'Stylish T-Shirt', price: 29.99, salePrice: 19.99, image: '/images/tshirt.jpg', category: 'Men' },
    { id: 2, name: 'Summer Dress', price: 49.99, salePrice: 39.99, image: '/images/dress.jpg', category: 'Women' },
    { id: 3, name: 'Kid\'s Jeans', price: 39.99, salePrice: 29.99, image: '/images/kidsjeans.jpg', category: 'Kids' },
    // Add more products as needed
  ]);

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  // Pagination Logic
  const totalPages = Math.ceil(products.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = products.slice(indexOfFirstItem, indexOfLastItem);

  // Filter functionality can be implemented here
  // For simplicity, this example does not include filtering

  return (
    <div className="container my-5">
      <h2 className="text-center" style={{ fontFamily: 'Poppins', fontWeight: 'bold', fontSize: '2.5rem', marginBottom: '40px' }}>Shop Our Collection</h2>
      
      <div className="row">
        {currentItems.map((product) => (
          <div key={product.id} className="col-md-4">
            <ProductCard product={product} />
          </div>
        ))}
      </div>

      {/* Pagination */}
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={setCurrentPage} />
    </div>
  );
};

export default Shop;
