// src/pages/Shop.jsx
import React, { useState } from 'react';
import ProductCard from '../components/ProductCard'; // Import the ProductCard component
import Pagination from '../components/Pagination'; // Assuming you have a Pagination component

const Shop = () => {
  const [activeCategory, setActiveCategory] = useState('Men');
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 9; // Display 9 products per page

  const products = {
    Men: [
      { id: 1, name: 'Stylish T-Shirt', price: 29.99, salePrice: 19.99, image: '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg' },
      { id: 2, name: 'Casual Shirt', price: 39.99, salePrice: 29.99, image: '/images/hipster-vacation-lake.jpg' },
      { id: 3, name: 'Formal Pants', price: 49.99, salePrice: 39.99, image: '/images/young-athletic-male-with-black-shirt-holding-skateboard-looking-aside.jpg' },
      { id: 4, name: 'Stylish T-Shirt', price: 29.99, salePrice: 19.99, image: '/images/tshirt.jpg' },
      { id: 5, name: 'Casual Shirt', price: 39.99, salePrice: 29.99, image: '/images/casual-shirt.jpg' },
      { id: 6, name: 'Formal Pants', price: 49.99, salePrice: 39.99, image: '/images/formal-pants.jpg' },
      { id: 7, name: 'Stylish T-Shirt', price: 29.99, salePrice: 19.99, image: '/images/tshirt.jpg' },
      { id: 8, name: 'Casual Shirt', price: 39.99, salePrice: 29.99, image: '/images/casual-shirt.jpg' },
      { id: 9, name: 'Formal Pants', price: 49.99, salePrice: 39.99, image: '/images/formal-pants.jpg' },
      // Add more products as needed
    ],
    Women: [
      { id: 10, name: 'Summer Dress', price: 49.99, salePrice: 39.99, image: '/images/dress.jpg' },
      { id: 11, name: 'Blouse', price: 29.99, salePrice: 24.99, image: '/images/blouse.jpg' },
      { id: 12, name: 'Skirt', price: 34.99, salePrice: 29.99, image: '/images/skirt.jpg' },
      { id: 13, name: 'Summer Dress', price: 49.99, salePrice: 39.99, image: '/images/dress.jpg' },
      { id: 14, name: 'Blouse', price: 29.99, salePrice: 24.99, image: '/images/blouse.jpg' },
      { id: 15, name: 'Skirt', price: 34.99, salePrice: 29.99, image: '/images/skirt.jpg' },
      { id: 16, name: 'Summer Dress', price: 49.99, salePrice: 39.99, image: '/images/dress.jpg' },
      { id: 17, name: 'Blouse', price: 29.99, salePrice: 24.99, image: '/images/blouse.jpg' },
      { id: 18, name: 'Skirt', price: 34.99, salePrice: 29.99, image: '/images/skirt.jpg' },
      // Add more products as needed
    ],
    Kids: [
      { id: 19, name: 'Kid\'s T-Shirt', price: 19.99, salePrice: 14.99, image: '/images/kids-tshirt.jpg' },
      { id: 20, name: 'Kid\'s Jeans', price: 29.99, salePrice: 24.99, image: '/images/kids-jeans.jpg' },
      { id: 21, name: 'Kid\'s Jacket', price: 39.99, salePrice: 34.99, image: '/images/kids-jacket.jpg' },
      { id: 22, name: 'Kid\'s T-Shirt', price: 19.99, salePrice: 14.99, image: '/images/kids-tshirt.jpg' },
      { id: 23, name: 'Kid\'s Jeans', price: 29.99, salePrice: 24.99, image: '/images/kids-jeans.jpg' },
      { id: 24, name: 'Kid\'s Jacket', price: 39.99, salePrice: 34.99, image: '/images/kids-jacket.jpg' },
      { id: 25, name: 'Kid\'s T-Shirt', price: 19.99, salePrice: 14.99, image: '/images/kids-tshirt.jpg' },
      { id: 26, name: 'Kid\'s Jeans', price: 29.99, salePrice: 24.99, image: '/images/kids-jeans.jpg' },
      { id: 27, name: 'Kid\'s Jacket', price: 39.99, salePrice: 34.99, image: '/images/kids-jacket.jpg' },
      // Add more products as needed
    ],
  };



  // Get current products
  const currentProducts = products[activeCategory].slice(
    (currentPage - 1) * productsPerPage,
    currentPage * productsPerPage
  );

  // Calculate total pages
  const totalPages = Math.ceil(products[activeCategory].length / productsPerPage);

  const containerStyle = {
    margin: '0 auto',
    padding: '20px',
  };

  const headingStyle = {
    fontFamily: 'Poppins, sans-serif',
    fontWeight: 'bold',
    fontSize: '2.5rem',
    textAlign: 'center',
    margin: '30px 0',
    color: '#224F34',
  };

  const buttonStyle = {
    margin: '0 10px',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: 'transparent',
    color: '#224F34',
    cursor: 'pointer',
    transition: 'color 0.3s, text-decoration 0.3s',
  };

  const buttonHoverStyle = {
    textDecoration: 'underline',
    color: 'green',
  };

  const productGridStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  };

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Shop Our Collection</h2>

      {/* Filter Links */}
      <div className="text-center mb-4">
        {['Men', 'Women', 'Kids'].map((category) => (
          <button
            key={category}
            style={buttonStyle}
            onMouseEnter={(e) => Object.assign(e.target.style, buttonHoverStyle)}
            onMouseLeave={(e) => Object.assign(e.target.style, buttonStyle)}
            onClick={() => setActiveCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div style={productGridStyle}>
        {currentProducts.map((product) => (
          <div key={product.id} style={{ width: '30%', marginBottom: '20px' }}>
            <ProductCard product={product} />
          </div>
        ))}
      </div>

      {/* Pagination */}
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={setCurrentPage} />
    </div>
  );
};

export default Shop;
