import React from 'react';
import Slider from 'react-slick'; // Assuming you'll use react-slick for carousel

const About = () => {

  // Styles for the about us page
  const sectionStyle = {
    padding: '50px 0',
  };

  const headingStyle = {
    fontSize: '3rem',
    fontWeight: 'bold',
    marginBottom: '30px',
    color: '#224F34',
    textAlign: 'center',
    fontFamily: 'Poppins, sans-serif',
  };

  const paragraphStyle = {
    fontSize: '1.4rem',
    fontWeight: 'bold',
    marginRight: '10px',
    lineHeight: '1.8',
    color: '#6F6A42',
  };

  const imageStyle = {
    width: '100%',
    height: '500px',
    borderRadius: '10px',
    boxShadow: '0px 4px 15px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease-in-out',
  };

  const handleMouseEnter = (e) => {
    e.target.style.transform = 'scale(1.05)';
  };

  const handleMouseLeave = (e) => {
    e.target.style.transform = 'scale(1)';
  };

  return (
    <div>
      {/* About Us Section */}
      <section className="container" style={sectionStyle}>
        <h2 style={headingStyle}>About Us</h2>
        <div className="row align-items-start"> {/* Aligned content to the top */}
          <div className="col-lg-12 mb-5">
            <p style={paragraphStyle} className="text-center">
              Welcome to Logo – your ultimate destination for cool and trendy fashion! 
              At Logo, we are dedicated to providing the latest styles that resonate 
              with the vibrant and dynamic spirit of today's youth. Our collection is 
              carefully curated to ensure that every piece embodies the cutting-edge 
              trends and timeless coolness that define your unique style.
            </p>
          </div>
          <div className="col-lg-12 text-center"> {/* Centered the image */}
            <img
              src="/images/IMG-20240723-WA0024.jpg"
              alt="About Us"
              style={imageStyle}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            />
          </div>
        </div>
      </section>

      {/* Our Mission Section */}
      <section className="container-fluid" style={{ backgroundColor: '#f0f0f0', padding: '60px 0' }}>
        <h2 style={headingStyle}>Our Mission</h2>
        <div className="row align-items-center">
          <div className="col-lg-6 text-center">
            <img
              src="/images/IMG-20240723-WA0026.jpg"
              alt="Our Mission"
              style={imageStyle}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            />
          </div>
          <div className="col-lg-6">
            <p style={paragraphStyle}>
              Our mission is simple: to inspire and empower youngsters to express themselves 
              through fashion. Whether you're looking for the perfect outfit for a night out, 
              a casual day at school, or anything in between, Logo has you covered. We believe 
              in fashion as a form of self-expression, and our diverse range of clothing is 
              designed to cater to every mood and moment.
            </p>
          </div>
        </div>
      </section>

      {/* Our Team Section */}
      <section className="container" style={sectionStyle}>
        <h2 style={headingStyle}>Our Team</h2>
        <Slider {...{ dots: true, infinite: true, slidesToShow: 3, slidesToScroll: 1, autoplay: true }}>
          <div>
            <img
              src="/images/IMG-20240723-WA0020.jpg"
              alt="Team Member 1"
              style={{ ...imageStyle, width: '300px', height: '300px' }}
            />
          </div>
          <div>
            <img
              src="/images/IMG-20240723-WA0022.jpg"
              alt="Team Member 2"
              style={{ ...imageStyle, width: '300px', height: '300px' }}
            />
          </div>
          <div>
            <img
              src="/images/IMG-20240723-WA0020.jpg"
              alt="Team Member 3"
              style={{ ...imageStyle, width: '300px', height: '300px' }}
            />
          </div>
          <div>
            <img
              src="/images/IMG-20240723-WA0022.jpg"
              alt="Team Member 4"
              style={{ ...imageStyle, width: '300px', height: '300px' }}
            />
          </div>
          <div>
            <img
              src="/images/IMG-20240723-WA0020.jpg"
              alt="Team Member 5"
              style={{ ...imageStyle, width: '300px', height: '300px' }}
            />
          </div>
        </Slider>
      </section>

      {/* Newsletter Section */}
      <section className="container-fluid text-center" style={{ backgroundColor: '#f0f0f0', padding: '60px 0' }}>
        <h2 style={headingStyle}>Stay Updated</h2>
        <p style={{paragraphStyle, maxWidth: '600px'}} className="mx-auto" >
          Sign up for our newsletter to get the latest updates on new arrivals and exclusive deals.
        </p>
        <div className="d-flex justify-content-center">
          <div style={{ backgroundColor: 'black', borderRadius: '10px', padding: '30px', maxWidth: '600px', width: '100%' }}>
            <h3 style={{ color: 'white', marginBottom: '20px' }}>Subscribe Now</h3>
            <p style={{ color: 'white', marginBottom: '30px' }}>
              Enter your email address and stay connected with the latest trends.
            </p>
            <div style={{ display: 'flex', alignItems: 'center', backgroundColor: '#fff', borderRadius: '5px', overflow: 'hidden' }}>
              <input
                type="email"
                placeholder="Enter email address"
                style={{
                  padding: '10px',
                  width: '70%',
                  border: 'none',
                  fontSize: '1rem',
                  outline: 'none',
                }}
              />
              <button
                style={{
                  backgroundColor: 'black',
                  color: 'white',
                  padding: '10px 20px',
                  border: 'none',
                  borderRadius: '0 5px 5px 0', // Rounded only on right corners
                  fontSize: '1rem',
                }}
              >
                Continue
              </button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default About;
