import React from 'react';
import { BsBag } from "react-icons/bs";

const Navbar = () => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light" style={{ 
      background: 'linear-gradient(135deg, #EFE8C2, #F7F3E7)', // Match Hero background
    }}>
      <div className="container">
        {/* Left: Logo/Anchor text */}
        <a className="navbar-brand fw-bold" href="#" style={{ color: '#224F34', fontFamily: 'Poppins', fontWeight: 'bold', fontSize: '1.5rem' }}>
          LOGO
        </a>
        
        {/* Toggle button for collapsing navbar in mobile view */}
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarNav" 
          aria-controls="navbarNav" 
          aria-expanded="false" 
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        
        {/* Centered Links */}
        <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
          <ul className="navbar-nav">
            {['Home', 'Shop', 'About Us', 'Contact'].map((link, index) => (
              <li className="nav-item" key={index}>
                <a 
                  className="nav-link" 
                  href="#" 
                  style={{ 
                    color: '#224F34', 
                    fontFamily: 'Poppins', 
                    textDecoration: 'none', 
                    fontWeight: 'bold', // Make bold
                    fontSize: '1.2rem', // Increase size
                  }}
                  onMouseOver={(e) => e.currentTarget.style.color = 'white'} // Change text color to white on hover
                  onMouseOut={(e) => e.currentTarget.style.color = '#224F34'} // Change back to original color on mouse out
                >
                  {link}
                </a>
              </li>
            ))}
          </ul>
        </div>
        
        {/* Right: Cart Icon and Login Button */}
        <div className="d-flex align-items-center">
          <a href="#" className="nav-link position-relative" style={{ color: '#224F34', fontWeight: 'bold', fontSize: '1.2rem' }}>
            {/* Cart Icon */}
            <BsBag size={28} /> {/* Increased size */}
            {/* Optional: Badge for number of items in cart */}
            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
              3
            </span>
          </a>
          
          {/* Login Button */}
          <a href="#" className="btn btn-outline-success ms-3" style={{ color: '#224F34', borderColor: '#224F34', fontWeight: 'bold', fontSize: '1.2rem' }}>
            Login
          </a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
