import React from 'react';
import Slider from 'react-slick'; // Import React Slick

const BestSelling = () => {
  const products = [
    { id: 1, name: 'Summer Wind T-shirt', price: '$31.99', rating: 4.2, image: '/images/IMG-20240723-WA0020.jpg' }, // Updated image path
    { id: 2, name: 'Tailored Jacket', price: '$41.03', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 3, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
    { id: 4, name: 'Classic Top', price: '$24.54', rating: 3.8, image: 'https://via.placeholder.com/200' },
    { id: 5, name: 'White Solid T-shirt', price: '$42.09', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 6, name: 'Formal Jeans', price: '$38.99', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 7, name: 'Product 7', price: '$38.99', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 8, name: 'Product 8', price: '$56.59', rating: 5.0, image: 'https://via.placeholder.com/200' },
  ];

  // Slick Carousel settings
  const settings = {
    autoplay: true,
    autoplaySpeed: 2000, // Slide every 2 seconds
    cssEase: "ease-in-out",  // Smooth transition effect
    dots: true,              // Show dots for navigation
    infinite: true,          // Infinite scrolling
    speed: 500,              // Transition speed
    slidesToShow: 4,         // Number of items to show
    slidesToScroll: 1,       // Number of items to slide at a time
    responsive: [
      {
        breakpoint: 1024,   // For medium-sized screens, show 3 items
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
        }
      },
      {
        breakpoint: 768,    // For small-sized screens, show 2 items
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 480,    // For very small screens, show 1 item
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };

  const carouselItemStyle = {
    padding: '15px',
  };

  const carouselImageStyle = {
    width: '100%',
    height: 'auto',
  };

  const headingStyle = {
    color: '#6F6A42', // Heading color
    fontFamily: 'Libre Baskerville, serif', // Font for heading
    textAlign: 'center',
  };

  const productNameStyle = {
    textAlign: 'center', // Center product name
    fontFamily: 'Libre Baskerville, serif', // Font for product name
  };

  const priceRatingStyle = {
    display: 'flex',
    justifyContent: 'center', // Center price and rating
    alignItems: 'center',
    paddingTop: '10px',
    marginTop: '10px',
    fontFamily: 'Libre Baskerville, serif', // Font for price and rating
  };

  const priceStyle = {
    fontWeight: 'bold', // Bold price
    fontSize: '1.2rem', // Increased size for price
  };

  const ratingStyle = {
    fontWeight: 'bold', // Bold rating
    fontSize: '1.2rem', // Increased size for rating
    marginLeft: '10px', // Space between price and rating
  };

  const separatorStyle = {
    height: '20px', // Height for the line
    borderLeft: '1px solid #ccc', // Line color and style
    margin: '0 10px', // Space around the line
  };

  const buttonStyle = {
    backgroundColor: 'white',
    color: '#6F6A42',
    border: '1px solid #224F34',
    padding: '12px 24px', // Adjusted for large size
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    display: 'block', // Ensures button takes full width for centering
    margin: '20px auto', // Center button with margin
  };

  return (
    <div className="container-fluid my-5">
      {/* Centered Title and Subtitle */}
      <div className="text-center mb-5">
        <h2 style={headingStyle}>Best Selling</h2>
        <p className="text-black">Get in on the trend with our curated selection of best-selling styles</p>
      </div>

      {/* React Slick Carousel */}
      <Slider {...settings}>
        {products.map((product) => (
          <div key={product.id}>
            <div style={carouselItemStyle}>
              <img
                src={product.image}
                alt={product.name}
                style={carouselImageStyle}
              />
            </div>
            <div className="text-center" style={productNameStyle}>
              <h5>{product.name}</h5>
              <div style={priceRatingStyle}>
                <span style={priceStyle}>{product.price}</span>
                <div style={separatorStyle} />
                <span style={ratingStyle}>{product.rating} ⭐</span>
              </div>
            </div>
            <button style={buttonStyle}>View Product</button>
          </div>
        ))}
      </Slider>

      {/* See More Button */}
      <div className="text-center mt-4 p-5">
        <a href="#" className="btn btn-primary ">
          See More <i className="bi bi-arrow-right"></i> {/* Arrow Icon */}
        </a>
      </div>
    </div>
  );
};

export default BestSelling;
