import React from 'react';

const HeroSection = () => {
  // Styles for the Hero Section
  const heroSectionStyle = {
    minHeight: '80vh',
    display: 'flex',
    alignItems: 'center',
    padding: '40px 0',
    backgroundColor: '#EFE8C2', // Background color
    fontFamily: 'Euclid Circular A, sans-serif', // Custom font
  };

  const heroTextStyle = {
    color: '#6F6A42', // Text color
  };

  const buttonStyle = {
    backgroundColor: '#6F6A42', // Button background color
    color: '#FFFFFF', // Button text color
    padding: '10px 20px',
    border: 'none',
    borderRadius: '8px', // Rounded corners
    fontSize: '1.2rem',
    fontWeight: 'bold',
    cursor: 'pointer',
  };

  const heroImageContainerStyle = {
    backgroundColor: '#6F6A42', // Image background color
    padding: '20px',
    borderRadius: '15px', // Rounded corners
  };

  const heroImageStyle = {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px', // Rounded image corners
  };

  return (
    <section className="container" style={heroSectionStyle}>
      <div className="row align-items-center">
        {/* Left: Text Section */}
        <div className="col-lg-6">
          <h1 className="display-4 fw-bold" style={heroTextStyle}>
            Discover Amazing Products
          </h1>
          <h4 className="my-4" style={heroTextStyle}>
            Find the perfect item that suits your style
          </h4>
          <a href="#" className="btn btn-primary btn-lg" style={buttonStyle}>
            Explore Now
          </a>
        </div>

        {/* Right: Image Section */}
        <div className="col-lg-6 text-center" style={heroImageContainerStyle}>
          <img
            src="https://via.placeholder.com/500x400"
            alt="Product"
            style={heroImageStyle}
          />
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
