import React, { useState } from 'react';
import Slider from 'react-slick';
// Import other components as needed

const Home = () => {
  // State to manage hover for each product button
  const [hoverStates, setHoverStates] = useState(Array(products.length).fill(false));

  // State to manage hover for the "See More" button
  const [seeMoreHover, setSeeMoreHover] = useState(false);

  const products = [
    { id: 1, name: 'Summer Wind T-shirt', price: '$31.99', rating: 4.2, image: '/images/IMG-20240723-WA0029.jpg' },
    { id: 2, name: 'Tailored Jacket', price: '$41.03', rating: 5.0, image: '/images/IMG-20240723-WA0021.jpg' },
    { id: 3, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: '/images/IMG-20240723-WA0027.jpg' },
    { id: 4, name: 'Classic Top', price: '$24.54', rating: 3.8, image: '/images/IMG-20240723-WA0024.jpg' },
    { id: 5, name: 'White Solid T-shirt', price: '$42.09', rating: 5.0, image: '/images/IMG-20240723-WA0029.jpg' },
    { id: 6, name: 'Formal Jeans', price: '$38.99', rating: 5.0, image: '/images/IMG-20240723-WA0023.jpg' },
    { id: 7, name: 'Product 7', price: '$38.99', rating: 5.0, image: '/images/IMG-20240723-WA0027.jpg' },
    { id: 8, name: 'Product 8', price: '$56.59', rating: 5.0, image: '/images/IMG-20240723-WA0025.jpg' },
  ];

  // Slick Carousel settings
  const settings = {
    autoplay: true,
    autoplaySpeed: 2000,
    cssEase: "ease-in-out",
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };

  // Other styles
  const heroSectionStyle = {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '50px 20px',
    background: 'linear-gradient(135deg, #EFE8C2, #F7F3E7)',
    fontFamily: 'Euclid Circular A, sans-serif',
  };

  const buttonStyle = {
    backgroundColor: '#6F6A42',
    color: '#FFFFFF',
    padding: '12px 30px',
    borderRadius: '8px',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    border: 'none',
    cursor: 'pointer',
    marginTop: '20px',
  };

  const handleMouseEnter = (index) => {
    const newHoverStates = [...hoverStates];
    newHoverStates[index] = true;
    setHoverStates(newHoverStates);
  };

  const handleMouseLeave = (index) => {
    const newHoverStates = [...hoverStates];
    newHoverStates[index] = false;
    setHoverStates(newHoverStates);
  };

  const getButtonStyle = (isHovered) => ({
    backgroundColor: isHovered ? '#d3d3d3' : 'white',
    color: '#6F6A42',
    border: '1px solid #224F34',
    padding: '12px 24px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    display: 'block',
    margin: '20px auto',
    transition: 'background-color 0.3s ease',
  });

  return (
    <div>
      <section className="container-fluid" style={heroSectionStyle}>
        <div className="row align-items-center">
          <div className="col-lg-6 text-center">
            <h1 className="display-4 fw-bold" style={{ fontSize: '2.5rem', color: '#6F6A42' }}>Discover Amazing Products</h1>
            <h4 className="my-4" style={{ fontSize: '1.5rem', color: '#6F6A42' }}>Find the perfect item that suits your style</h4>
            <button style={buttonStyle}>Explore Now</button>
          </div>
          <div className="col-lg-6 text-center">
            <img
              src="/images/IMG-20240723-WA0030.jpg" // Image path from public folder
              alt="Product Image"
              style={{ width: '100%', height: 'auto', borderRadius: '10px' }}
            />
          </div>
        </div>
      </section>

      <div className="container-fluid my-5">
        <div className="text-center mb-5">
          <h2 style={{ color: '#6F6A42', fontFamily: 'Libre Baskerville, serif', textAlign: 'center', fontWeight: 'bold', fontSize: '4rem' }}>Best Selling</h2>
          <p className="text-black">Get in on the trend with our curated selection of best-selling styles</p>
        </div>

        <Slider {...settings}>
          {products.map((product, index) => (
            <div key={product.id}>
              <div style={{ padding: '15px' }}>
                <div 
                  style={{ 
                    overflow: 'hidden', 
                    transition: 'transform 0.3s', 
                    transform: hoverStates[index] ? 'scale(1.05)' : 'scale(1)',
                  }}
                  onMouseEnter={() => handleMouseEnter(index)}
                  onMouseLeave={() => handleMouseLeave(index)}
                >
                  <img
                    src={product.image}
                    alt={product.name}
                    style={{ width: '100%', height: '400px', objectFit: 'cover' }}
                  />
                </div>
              </div>
              <div className="text-center" style={{ textAlign: 'center', fontFamily: 'Libre Baskerville, serif' }}>
                <h5>{product.name}</h5>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                  <span style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{product.price}</span>
                  <span style={{ height: '20px', borderLeft: '1px solid #ccc', margin: '0 10px' }} />
                  <span style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{product.rating} ⭐</span>
                </div>
              </div>
              <button style={getButtonStyle(hoverStates[index])}>View Product</button>
            </div>
          ))}
        </Slider>

        {/* See More Button */}
        <div className="text-center mt-4 p-5">
          <a href="#" style={getButtonStyle(seeMoreHover)} onMouseEnter={() => setSeeMoreHover(true)} onMouseLeave={() => setSeeMoreHover(false)}>
            See More <i className="bi bi-arrow-right"></i>
          </a>
        </div>
      </div>
    </div>
  );
};

export default Home;
