import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.css'; // Ensure to include styles

const ProductDetail = () => {
  const { id } = useParams(); // Extract product ID from URL
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [productData, setProductData] = useState(null); // Initialize with null for later checking
  const [mainImage, setMainImage] = useState('');

  // Fetch product data based on the ID
  useEffect(() => {
    const fetchedProductData = getProductById(id);
    setProductData(fetchedProductData);

    if (fetchedProductData) {
      setMainImage(fetchedProductData.images[0]); // Set initial main image
    }
  }, [id]); // Run the effect when the component mounts or id changes

  // If product data is not found, render a message
  if (!productData) {
    return <div className="not-found">Product not found!</div>;
  }

  return (
    <div className="product-detail container">
      <div className="row align-items-start"> {/* Use align-items-start to align items to the top */}
        <div className="col-lg-6">
          <div className="product-images">
            <img src={mainImage} alt={productData.name} className="main-image" />
            <div className="image-swatches">
              {productData.images.map((image, index) => (
                <img
                  key={index}
                  src={image}
                  alt={`${productData.name} ${index + 1}`}
                  onClick={() => setMainImage(image)}
                  className="swatch"
                />
              ))}
            </div>
          </div>
        </div>

        <div className="col-lg-6 product-info align-left text-start"> {/* Align text to left */}
          <h1 className="product-title">{productData.name}</h1>
          <p className="brand">Brand: {productData.brand}</p>
          <p className="seller">Sold by: {productData.seller}</p>
          <div className="rating">Rating: {productData.rating} (Reviews: {productData.reviews})</div>
          <div className="pricing">
            <span className="sale-price">${productData.salePrice.toFixed(2)}</span>
            <span className="original-price">${productData.price.toFixed(2)}</span>
            <span className="discount">30% off</span>
          </div>

          {/* Size Selection */}
          <div className="size-selection">
            <label>Select Size</label>
            <div>
              {['S', 'M', 'L', 'XL'].map((size) => (
                <button
                  key={size}
                  className={`size-button ${selectedSize === size ? 'active' : ''}`}
                  onClick={() => setSelectedSize(size)}
                >
                  {size}
                </button>
              ))}
            </div>
            <a href="/size-chart" className="size-chart-link">Size Chart</a>
          </div>

          {/* Color Selection */}
          <div className="color-selection">
            <label>Select Color</label>
            <div>
              {productData.colors.map((color) => (
                <button
                  key={color}
                  className={`color-swatch ${selectedColor === color ? 'active' : ''}`}
                  onClick={() => setSelectedColor(color)}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
          </div>

          {/* Best Offers */}
          <div className="offers">
            <h3>Best Offers</h3>
            <ul>
              <li>Special offer: Get 25% off T&C</li>
              <li>Bank offer: Get 30% off on Axis Bank Credit card T&C</li>
              <li>Wallet offer: Get 40% cashback via Paytm wallet on first transaction T&C</li>
            </ul>
          </div>

          {/* Add to Cart and Wishlist */}
          <div className="actions">
            <button className="add-to-cart">Add to Cart</button>
            <button className="wishlist-icon">♥</button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Dummy function to simulate fetching product by ID
function getProductById(id) {
  const sampleProducts = [
    {
      id: '1',
      name: 'Stylish Shirt',
      brand: 'Brand A',
      seller: 'Seller XYZ',
      rating: 4.5,
      reviews: 120,
      price: 99.99,
      salePrice: 69.99,
      images: [
        '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg',
        '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg',
        '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg',
        '/images/front-view-smiley-man-holding-lifesaving-buoy.jpg',
      ],
      colors: ['#ff0000', '#00ff00', '#0000ff'],
    },
    // Add more products as needed
  ];

  return sampleProducts.find((product) => product.id === id);
}

export default ProductDetail;
