import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import './ProductDetail.css'; // Importing advanced CSS styles

const ProductDetail = () => {
  const { id } = useParams();
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [tab, setTab] = useState('productDetails');
  const productData = getProductById(id);
  const [mainImage, setMainImage] = useState(productData.images[0]);

  const similarProducts = getSimilarProducts(id);
  const customerLikes = getCustomerLikes(id);

  // Settings for the carousel
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    arrows: true,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 3 } },
      { breakpoint: 768, settings: { slidesToShow: 2 } },
      { breakpoint: 480, settings: { slidesToShow: 1 } },
    ],
  };

  return (
    <div className="product-detail-container">
      <div className="product-detail">
        {/* Left Section: Product Details */}
        <div className="product-info">
          <h1 className="product-title">{productData.name}</h1>
          <p className="brand">Brand: {productData.brand}</p>
          <p className="seller">Sold by: {productData.seller}</p>
          <p className="rating">Rating: {productData.rating} (Reviews: {productData.reviews})</p>
          <p className="pricing">
            <span className="sale-price">${productData.salePrice}</span>
            <span className="original-price">${productData.price}</span>
            <span className="discount">30% off</span>
          </p>

          {/* Size Selection */}
          <div className="size-selection">
            <label>Select Size</label>
            <div>
              {['S', 'M', 'L', 'XL'].map((size) => (
                <button
                  key={size}
                  className={`size-button ${selectedSize === size ? 'active' : ''}`}
                  onClick={() => setSelectedSize(size)}
                >
                  {size}
                </button>
              ))}
            </div>
            <a href="/size-chart" className="size-chart-link">Size Chart</a>
          </div>

          {/* Color Selection */}
          <div className="color-selection">
            <label>Select Color</label>
            <div>
              {productData.colors.map((color) => (
                <button
                  key={color}
                  className={`color-swatch ${selectedColor === color ? 'active' : ''}`}
                  onClick={() => setSelectedColor(color)}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
          </div>

          {/* Offers */}
          <div className="offers">
            <h3>Best Offers</h3>
            <ul>
              <li>Special offer: Get 25% off T&C</li>
              <li>Bank offer: Get 30% off on Axis Bank Credit card T&C</li>
              <li>Wallet offer: Get 40% cashback via Paytm wallet on first transaction T&C</li>
            </ul>
          </div>

          {/* Add to Cart and Wishlist */}
          <div className="actions">
            <button className="add-to-cart">Add to Cart</button>
            <button className="wishlist-icon">â™¥</button>
          </div>
        </div>

        {/* Right Section: Product Images */}
        <div className="product-images">
          <img src={mainImage} alt={productData.name} className="main-image" />
          <div className="image-swatches">
            {productData.images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`${productData.name} ${index + 1}`}
                onClick={() => setMainImage(image)}
                className="swatch"
              />
            ))}
          </div>
        </div>
      </div>

      {/* Tabs Section */}
      <div className="tabs">
        <div className="tab-headings">
          <div
            className={`tab ${tab === 'productDetails' ? 'active' : ''}`}
            onClick={() => setTab('productDetails')}
          >
            Product Details
          </div>
          <div
            className={`tab ${tab === 'specs' ? 'active' : ''}`}
            onClick={() => setTab('specs')}
          >
            Specs
          </div>
          <div
            className={`tab ${tab === 'reviews' ? 'active' : ''}`}
            onClick={() => setTab('reviews')}
          >
            Ratings and Reviews
          </div>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {tab === 'productDetails' && <p>{productData.description}</p>}
          {tab === 'specs' && <p>{productData.specs}</p>}
          {tab === 'reviews' && <p>Customer reviews and ratings will appear here.</p>}
        </div>
      </div>

      {/* Similar Products Section */}
      <div className="similar-products">
        <h2>Similar Products</h2>
        <Slider {...settings}>
          {similarProducts.map((product) => (
            <div key={product.id} className="carousel-item">
              <img src={product.image} alt={product.name} className="carousel-image" />
              <h5>{product.name}</h5>
              <p>{product.brand}</p>
              <div className="pricing">
                <span className="sale-price">${product.salePrice}</span>
                <span className="original-price">${product.price}</span>
                <span className="discount">30% off</span>
              </div>
            </div>
          ))}
        </Slider>
      </div>

      {/* Customers Also Like Section */}
      <div className="customers-also-like">
        <h2>Customers Also Like</h2>
        <Slider {...settings}>
          {customerLikes.map((product) => (
            <div key={product.id} className="carousel-item">
              <img src={product.image} alt={product.name} className="carousel-image" />
              <h5>{product.name}</h5>
              <p>{product.brand}</p>
              <div className="pricing">
                <span className="sale-price">${product.salePrice}</span>
                <span className="original-price">${product.price}</span>
                <span className="discount">30% off</span>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

// Dummy function to simulate fetching product by ID
