import React from 'react';
import { Carousel } from 'react-bootstrap';  

const BestSelling = () => {
  const products = [
    { id: 1, name: 'Summer Wind T-shirt', price: '$31.99', rating: 4.2, image: 'https://via.placeholder.com/200' },
    { id: 2, name: 'Tailored Jacket', price: '$41.03', rating: 5.0, image: 'https://via.placeholder.com/200' },
    { id: 3, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },
    { id: 4, name: 'Formal Classic Suit', price: '$50.00', rating: 4.0, image: 'https://via.placeholder.com/200' },

];

  return (
    <div className="container my-5">
      {/* Centered Title and Subtitle */}
      <div className="text-center mb-5">
        <h2 className="fw-bold">Best Selling</h2>
        <p className="text-muted">Get in on the trend with our curated selection of best-selling styles</p>
      </div>

      {/* Product Carousel */}
      <Carousel>
        {products.map((product) => (
          <Carousel.Item key={product.id}>
            <div className="d-flex justify-content-center">
              <img
                className="d-block"
                src={product.image}
                alt={product.name}
                style={{ width: '200px', height: 'auto' }}
              />
            </div>
            <Carousel.Caption className="text-dark">
              <h5>{product.name}</h5>
              <p>{product.price}</p>
              <p>Rating: {product.rating}</p>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>

      {/* See More Button */}
      <div className="text-center mt-4">
        <a href="#" className="btn btn-primary">
          See More <i className="bi bi-arrow-right"></i> {/* Bootstrap Arrow Icon */}
        </a>
      </div>
    </div>
  );
};

export default BestSelling;
