import React from 'react';
import { useState, useEffect } from 'react';

const Banner = () => {
  // Countdown state
  const [countdown, setCountdown] = useState({ days: 0, hours: 0, minutes: 0 });

  useEffect(() => {
    // Set a deadline for the offer
    const deadline = new Date().getTime() + 86400000; // 1 day in milliseconds

    const interval = setInterval(() => {
      const now = new Date().getTime();
      const distance = deadline - now;

      // Calculate time left
      const days = Math.floor(distance / (1000 * 60 * 60 * 24));
      const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));

      // Update countdown state
      setCountdown({ days, hours, minutes });

      // Clear interval if countdown ends
      if (distance < 0) {
        clearInterval(interval);
        setCountdown({ days: 0, hours: 0, minutes: 0 });
      }
    }, 1000);

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []);

  return (
    <div className="container my-5">
      <div className="row align-items-center"  style={{  padding:'5%' }}>
        {/* Left: Image Section */}
        <div className="col-lg-6">
          <img 
            src="https://via.placeholder.com/400" 
            alt="Exclusive Offer" 
            className="img-fluid" 
            style={{ position: 'absolute', bottom: '', left: '5px',  }}
          />
        </div>

        {/* Right: Text Section */}
        <div className="col-lg-6 text-end" style={{padding:'8%'}}>
          <h2 className="fw-bold">Exclusive Offer</h2>
          <p className="text-muted">
            Unlock the unlimited style upgrade with our exclusive offer. Enjoy savings of up to 40% off on your latest New Arrivals.
          </p>

          {/* Countdown */}
          <div className="d-flex justify-content-end mb-4">
            <div className="text-center mx-2">
              <h4>{countdown.days}</h4>
              <small>Days</small>
            </div>
            <div className="text-center mx-2">
              <h4>{countdown.hours}</h4>
              <small>Hours</small>
            </div>
            <div className="text-center mx-2">
              <h4>{countdown.minutes}</h4>
              <small>Minutes</small>
            </div>
          </div>

          {/* Shop Now Button */}
          <div className="text-center">
            <a href="#" className="btn btn-primary btn-lg">Shop Now</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Banner;
