import React from 'react';
import { BsBag } from "react-icons/bs"; // Importing the bag icon from react-icons
import './Navbar.css'; // Assuming you will create a custom CSS file for navbar styles

const Navbar = () => {
  return (
    <nav className="navbar navbar-expand-lg" style={styles.navbar}>
      <div className="container">
        {/* Left: Logo/Anchor text */}
        <a className="navbar-brand fw-bold" href="#" style={styles.logo}>LOGO</a>
        
        {/* Centered Links */}
        <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a className="nav-link active" href="#" style={styles.link}>Home</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" style={styles.link}>Shop</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" style={styles.link}>Features</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" style={styles.link}>Contact</a>
            </li>
          </ul>
        </div>

        {/* Right: Cart Icon and Login Button */}
        <div className="d-flex">
          <a href="#" className="nav-link position-relative p-2" style={styles.cartIcon}>
          <BsBag  style={styles.cartIconStyle} />             {/* Optional: Badge for number of items in cart */}
            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
              3
            </span>
          </a>
          {/* Login Button */}
          <a href="#" className="btn" style={styles.loginButton}>Login</a>
        </div>
      </div>
    </nav>
  );
};

// Custom styles
const styles = {
  navbar: {
    backgroundColor: '#EFE8C2', // Navbar background color
    fontFamily: 'Poppins, sans-serif', // Poppins font
  },
  logo: {
    color: '#224F34', // Logo text color
    fontSize: '24px', // Increased size for the logo
    fontWeight: '700', // Bold logo
  },
  link: {
    color: '#224F34', // Navbar link color
    fontSize: '18px', // Increased size for links
    fontWeight: '500', // Bold links
  },
  cartIcon: {
    color: '#224F34', // Cart icon color
  },
  cartIconStyle: {
    fontSize: '28px', // Increased size for the cart icon
    transform: 'scale(1.2)', // Scale to make the icon appear thicker
  },
  loginButton: {
    backgroundColor: '#EFE8C2', // Login button background color
    border: '3px solid #224F34', // Border color
    color: '#224F34', // Text color
    marginLeft: '10px', // Margin for spacing
    fontSize: '16px', // Increased size for the button
    fontWeight: '500', // Bold button text
  },
};

export default Navbar;
