import React from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import { BsBag } from "react-icons/bs";

const Navbar = () => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light" style={{ 
      background: 'linear-gradient(135deg, #EFE8C2, #F7F3E7)', // Match Hero background
    }}>
      <div className="container">
        {/* Left: Logo/Anchor text */}
        <Link className="navbar-brand fw-bold" to="/" style={{ color: '#224F34', fontFamily: 'Poppins', fontWeight: 'bold', fontSize: '1.5rem' }}>
          LOGO
        </Link>
        
        {/* Toggle button for collapsing navbar in mobile view */}
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarNav" 
          aria-controls="navbarNav" 
          aria-expanded="false" 
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        
        {/* Centered Links */}
        <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link 
                className="nav-link" 
                to="/" 
                style={{ 
                  color: '#224F34', 
                  fontFamily: 'Poppins', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '1.2rem', 
                }}
                onMouseOver={(e) => e.currentTarget.style.color = 'white'}
                onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
              >
                HOME
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                className="nav-link" 
                to="/shop" 
                style={{ 
                  color: '#224F34', 
                  fontFamily: 'Poppins', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '1.2rem', 
                }}
                onMouseOver={(e) => e.currentTarget.style.color = 'white'}
                onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
              >
                SHOP
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                className="nav-link" 
                to="/about" 
                style={{ 
                  color: '#224F34', 
                  fontFamily: 'Poppins', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '1.2rem', 
                }}
                onMouseOver={(e) => e.currentTarget.style.color = 'white'}
                onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
              >
                ABOUT US
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                className="nav-link" 
                to="/contact" 
                style={{ 
                  color: '#224F34', 
                  fontFamily: 'Poppins', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '1.2rem', 
                }}
                onMouseOver={(e) => e.currentTarget.style.color = 'white'}
                onMouseOut={(e) => e.currentTarget.style.color = '#224F34'}
              >
                CONTACT
              </Link>
            </li>
          </ul>
        </div>
        
        {/* Right: Cart Icon and Login Button */}
        <div className="d-flex align-items-center">
          <Link to="/cart" className="nav-link position-relative" style={{ color: '#224F34', fontWeight: 'bold', fontSize: '1.2rem' }}>
            {/* Cart Icon */}
            <BsBag size={28} /> {/* Increased size */}
            {/* Optional: Badge for number of items in cart */}
            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
              3
            </span>
          </Link>
          
          {/* Login Button */}
          <Link to="/login" className="btn btn-outline-success ms-3" style={{ color: '#224F34', borderColor: '#224F34', fontWeight: 'bold', fontSize: '1.2rem' }}>
            Login
          </Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
