import React from 'react';

const ProductShowcase = () => {
  const products = [
    {
      id: 1,
      name: 'Stylish Jacket',
      description: 'Stay warm and stylish with our latest jacket that combines comfort with fashion.',
      image: '/images/model2.jpg',
    },
    {
      id: 2,
      name: 'Trendy Sneakers',
      description: 'Step into comfort with our trendy sneakers, perfect for any occasion.',
      image: '/images/model3.jpg',
    },
    {
      id: 3,
      name: 'Casual T-Shirt',
      description: 'A versatile t-shirt that pairs well with any outfit, made from high-quality fabric.',
      image: '/images/model4.jpg',
    },
  ];

  return (
    <div className="container my-5">
      <h2 className="fw-bold text-center mb-4" style={{ color: '#6F6A42', fontFamily: 'Poppins' }}>Featured Products</h2>
      <p className="text-center mb-5" style={{ color: '#6F6A42', fontFamily: 'Poppins' }}>
        Discover our selection of trending products that blend style and comfort. 
        Each piece is designed to elevate your wardrobe.
      </p>

      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-lg-4 col-md-6 mb-4">
            <div 
              className="card shadow border-light rounded text-center"
              style={{ 
                transition: 'transform 0.3s, box-shadow 0.3s', 
                cursor: 'pointer',
                border: 'none' // Remove default card border
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)'; // Scale effect on hover
                e.currentTarget.style.boxShadow = '0 10px 30px rgba(0, 0, 0, 0.2)'; // Increase shadow on hover
              }} 
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)'; // Reset scale on leave
                e.currentTarget.style.boxShadow = '0 4px 20px rgba(0, 0, 0, 0.1)'; // Reset shadow on leave
              }}
            >
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top img-fluid"
                style={{ borderRadius: '10px 10px 0 0', height: '400px', objectFit: 'cover' }} // Maintain height and fit image
              />
              <div className="card-body" style={{ backgroundColor: '#f8f9fa', borderRadius: '0 0 10px 10px' }}>
                <h5 className="card-title" style={{ fontFamily: 'Libre Baskerville', fontWeight: 'bold', fontSize: '1.5rem' }}>{product.name}</h5>
                <p className="card-text" style={{ fontFamily: 'Libre Baskerville', fontSize: '1rem' }}>{product.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* See More Button */}
      <div className="text-center mt-4">
        <a 
          href="#" 
          className="btn btn-primary btn-lg" 
          style={{ 
            backgroundColor: '#6F6A42', 
            color: 'white', 
            transition: 'background-color 0.3s',
            borderRadius: '5px',
            border: 'none',
          }}
          onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#5c5a3a')} // Darker shade on hover
          onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#6F6A42')} // Original color on leave
        >
          See More
        </a>
      </div>
    </div>
  );
};

export default ProductShowcase;
